// components/Dashboard.js
import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import './Dashboard.css';

const Dashboard = () => {
  const [productCount, setProductCount] = useState(0);
  const [orderCount, setOrderCount] = useState(0);

  useEffect(() => {
    // Fetch product count from your API or manage it in state
    // For demonstration, setting a dummy value here
    const fetchProductCount = () => {
      // Replace this with your actual API call or state management logic
      const dummyProductCount = 100; // Example product count
      setProductCount(dummyProductCount);
    };

    // Fetch order count from your API or manage it in state
    // For demonstration, setting a dummy value here
    const fetchOrderCount = () => {
      // Replace this with your actual API call or state management logic
      const dummyOrderCount = 50; // Example order count
      setOrderCount(dummyOrderCount);
    };

    fetchProductCount();
    fetchOrderCount();
  }, []);

  return (
    <div className="dashboard">
      <h1>Dashboard</h1>
      <ul>
        <li>Number of Products: {productCount}</li>
        <li>Number of Orders: {orderCount}</li>
        <li><Link to="/products">Products Management</Link></li>
        <li><Link to="/orders">Orders Management</Link></li>
        <li><Link to="/calendar">Orders Calendar</Link></li>
      </ul>
    </div>
  );
}

export default Dashboard;
