import React, { useState } from 'react';
import './OrdersCalendarView.css';

// Sample orders data
const initialOrders = [
  { id: 1, customerName: 'John Doe', deliveryDate: '2024-03-11', status: 'Pending' },
  { id: 2, customerName: 'Jane Smith', deliveryDate: '2024-03-10', status: 'Delivered' },
  { id: 3, customerName: 'Alice Johnson', deliveryDate: '2024-03-09', status: 'Processing' },
  { id: 4, customerName: 'Bob Johnson', deliveryDate: '2024-03-11', status: 'Processing' },
];

const OrdersCalendarView = () => {
  const [orders] = useState(initialOrders);
  const [selectedDate, setSelectedDate] = useState(null);
  const [ordersForSelectedDate, setOrdersForSelectedDate] = useState([]);

  const handleDateClick = (date) => {
    setSelectedDate(date);
    const ordersForDate = orders.filter(order => order.deliveryDate === date);
    setOrdersForSelectedDate(ordersForDate);
  };

  return (
    <div className="calendar">
      <h2>Orders Calendar View</h2>
      <div className="calendar-grid">
        {Array.from({ length: 31 }, (_, index) => {
          const day = index + 1;
          const year = 24; // For year 2024
          const formattedDay = day < 10 ? `0${day}` : day.toString();
          const formattedDate = `2024-03-${formattedDay}`; // Format: yyyy-mm-dd
          return (
            <div key={formattedDate} className={`date-box ${formattedDate === selectedDate ? 'selected' : ''}`} onClick={() => handleDateClick(formattedDate)}>
              <span className="date">{formattedDate}</span>
            </div>
          );
        })}
      </div>
      {selectedDate && (
        <div className="orders-for-date">
          <h3>Orders for {selectedDate}</h3>
          <ul>
            {ordersForSelectedDate.map(order => (
              <li key={order.id}>
                <strong>Order ID:</strong> {order.id}, <strong>Customer:</strong> {order.customerName}, <strong>Status:</strong> {order.status}
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}

export default OrdersCalendarView;
