import React from 'react';
import { Link } from 'react-router-dom';
import './Dashboard.css';

const Dashboard = ({ totalProductCount, totalOrderCount }) => {
  // If totalProductCount is initially 0, display 10 as default
  const displayedProductCount = totalProductCount === 0 ? 10 : totalProductCount;
  const displayedOrderCount = totalOrderCount === 0 ? 4 : totalOrderCount;

  return (
    <div className="dashboard">
      <h1>Dashboard</h1>
      <p>Total Products: {displayedProductCount}</p>
      <p>Total Orders: {displayedOrderCount}</p>
      <p>Summary of key metrics or features</p>
      <ul>
        <li><Link to="/products">Products Management</Link></li>
        <li><Link to="/orders">Orders Management</Link></li>
        <li><Link to="/calendar">Orders Calendar</Link></li>
      </ul>
    </div>
  );
}

export default Dashboard;
