import React, { useState } from 'react';
import './OrdersManagement.css';

const initialOrders = [
  { id: 1, customerName: 'John Doe', orderDate: '2024-03-11', status: 'Pending' },
  { id: 2, customerName: 'Jane Smith', orderDate: '2024-03-10', status: 'Delivered' },
  { id: 3, customerName: 'Alice Johnson', orderDate: '2024-03-09', status: 'Processing' }
];

const OrdersManagement = () => {
  const [orders, setOrders] = useState(initialOrders);
  const [selectedOrder, setSelectedOrder] = useState(null);
  const [editMode, setEditMode] = useState(false);
  const [editedStatus, setEditedStatus] = useState('');
  const [statusOptions] = useState(['Pending', 'Processing', 'Delivered', 'Cancelled']);

  const handleViewDetails = (order) => {
    setSelectedOrder(order);
  };

  const handleEditStatus = (order) => {
    setSelectedOrder(order);
    setEditedStatus(order.status);
    setEditMode(true);
  };

  const handleUpdateStatus = () => {
    const updatedOrders = orders.map(order =>
      order.id === selectedOrder.id ? { ...order, status: editedStatus } : order
    );
    setOrders(updatedOrders);
    setEditMode(false);
  };

  const handleCancelEdit = () => {
    setEditMode(false);
  };

  return (
    <div className="orders-container">
      <h2>Orders Management</h2>
      <div className="order-list">
        <ul>
          {orders.map(order => (
            <li key={order.id}>
              <span>Order ID: {order.id}</span>
              <span>Customer: {order.customerName}</span>
              <span>Date: {order.orderDate}</span>
              <span>Status: {order.status}</span>
              <button onClick={() => handleViewDetails(order)}>View Details</button>
              {!editMode && (
                <button onClick={() => handleEditStatus(order)}>Edit Status</button>
              )}
            </li>
          ))}
        </ul>
      </div>
      {selectedOrder && (
        <div className="order-details">
          <h3>Order Details</h3>
          <p><strong>Order ID:</strong> {selectedOrder.id}</p>
          <p><strong>Customer:</strong> {selectedOrder.customerName}</p>
          <p><strong>Date:</strong> {selectedOrder.orderDate}</p>
          <p><strong>Status:</strong> 
            {editMode ? (
              <select value={editedStatus} onChange={(e) => setEditedStatus(e.target.value)}>
                {statusOptions.map(status => (
                  <option key={status} value={status}>{status}</option>
                ))}
              </select>
            ) : (
              selectedOrder.status
            )}
            {editMode && (
              <React.Fragment>
                <button onClick={handleUpdateStatus}>Update</button>
                <button onClick={handleCancelEdit}>Cancel</button>
              </React.Fragment>
            )}
          </p>
        </div>
      )}
    </div>
  );
}

export default OrdersManagement;
