// components/OrdersCalendarView.js
import React, { useState } from 'react';
import './OrdersCalendarView.css';

const initialOrders = [
  { id: 1, customerName: 'John Doe', deliveryDate: '2024-03-11', status: 'Pending' },
  { id: 2, customerName: 'Jane Smith', deliveryDate: '2024-03-10', status: 'Delivered' },
  { id: 3, customerName: 'Alice Johnson', deliveryDate: '2024-03-09', status: 'Processing' }
];

const OrdersCalendarView = () => {
  const [orders] = useState(initialOrders);
  const [selectedDate, setSelectedDate] = useState(null);

  const handleDateClick = (date) => {
    setSelectedDate(date);
  };

  const getOrdersForDate = (date) => {
    return orders.filter(order => order.deliveryDate === date);
  };

  return (
    <div className="calendar">
      <h2>Orders Calendar View</h2>
      <div className="calendar-grid">
        {Array.from({ length: 31 }, (_, index) => {
          const date = `2024-03-${index + 1}`;
          const ordersForDate = getOrdersForDate(date);
          return (
            <div key={date} className={`date-box ${date === selectedDate ? 'selected' : ''}`} onClick={() => handleDateClick(date)}>
              <span className="date">{index + 1}</span>
              {ordersForDate.length > 0 && (
                <ul className="order-list">
                  {ordersForDate.map(order => (
                    <li key={order.id}>{order.customerName}</li>
                  ))}
                </ul>
              )}
            </div>
          );
        })}
      </div>
    </div>
  );
}

export default OrdersCalendarView;
