import React, { useState } from 'react';
import './OrdersCalendarView.css';

const initialOrders = [
  { id: 1, customerName: 'John Doe', deliveryDate: '11/03/24', status: 'Pending' },
  { id: 2, customerName: 'Jane Smith', deliveryDate: '10/03/24', status: 'Delivered' },
  { id: 3, customerName: 'Alice Johnson', deliveryDate: '09/03/24', status: 'Processing' },
  { id: 4, customerName: 'Bob Johnson', deliveryDate: '11/03/24', status: 'Processing' },
];

const OrdersCalendarView = () => {
  const [selectedDate, setSelectedDate] = useState(new Date());
  const [ordersForSelectedDate, setOrdersForSelectedDate] = useState([]);
  
  const orders = initialOrders;

  const handleDateClick = (date) => {
    const formattedDate = formatDate(new Date(date));
    const ordersForDate = orders.filter(order => {
      const orderDate = formatDate(new Date(order.deliveryDate));
      return orderDate === formattedDate;
    });
    setOrdersForSelectedDate(ordersForDate);
  };

  const getDaysInMonth = (year, month) => {
    return new Date(year, month + 1, 0).getDate();
  };

  const formatDate = (date) => {
    const year = date.getFullYear();
    const month = date.getMonth() + 1;
    const formattedMonth = month < 10 ? `0${month}` : `${month}`;
    const day = date.getDate();
    const formattedDay = day < 10 ? `0${day}` : `${day}`;
    return `${formattedDay}/${formattedMonth}/${year}`;
  };

  const getCalendarData = () => {
    const year = selectedDate.getFullYear();
    const month = selectedDate.getMonth();
    const daysInMonth = getDaysInMonth(year, month);
    const firstDayOfMonth = new Date(year, month, 1);
    const firstDayOfWeek = firstDayOfMonth.getDay();

    let date = 1;
    const calendarData = [];

    for (let i = 0; i < 6; i++) {
      const week = [];
      for (let j = 0; j < 7; j++) {
        if (i === 0 && j < firstDayOfWeek) {
          week.push('');
        } else if (date > daysInMonth) {
          break;
        } else {
          week.push(formatDate(new Date(year, month, date)));
          date++;
        }
      }
      calendarData.push(week);
      if (date > daysInMonth) break;
    }

    return calendarData;
  };

  const handleMonthChange = (change) => {
    setSelectedDate(prevDate => {
      const newDate = new Date(prevDate);
      newDate.setMonth(newDate.getMonth() + change);
      return newDate;
    });
  };

  const handleYearChange = (change) => {
    setSelectedDate(prevDate => {
      const newDate = new Date(prevDate);
      newDate.setFullYear(newDate.getFullYear() + change);
      return newDate;
    });
  };

  const getStatusClass = (date) => {
    const ordersForDate = orders.filter(order => formatDate(new Date(order.deliveryDate)) === date);
    const hasProcessingOrders = ordersForDate.some(order => order.status === 'Processing');
    const hasPendingOrders = ordersForDate.some(order => order.status === 'Pending');
  
    if (hasProcessingOrders) {
      return 'processing';
    } else if (hasPendingOrders) {
      return 'pending';
    } else {
      return '';
    }
  };
  

  const calendarData = getCalendarData();

  return (
    <div className="calendar">
      <h2>Orders Calendar View</h2>
      <div className="calendar-controls">
        <button onClick={() => handleYearChange(-1)}>Previous Year</button>
        <button onClick={() => handleMonthChange(-1)}>Previous Month</button>
        <span>{selectedDate.toLocaleString('default', { month: 'long', year: 'numeric' })}</span>
        <button onClick={() => handleMonthChange(1)}>Next Month</button>     
        <button onClick={() => handleYearChange(1)}>Next Year</button>
      </div>
      <div className="calendar-grid">
        {['Su', 'Mo', 'Tu', 'We', 'Th', 'Fr', 'Sa'].map(day => (
          <div key={day} className="day">{day}</div>
        ))}
        {calendarData.map((week, index) => (
          <React.Fragment key={index}>
            {week.map((date, dayIndex) => (
              <div key={dayIndex} className={`date-box ${date === '' ? 'empty' : ''} ${getStatusClass(date)}`} onClick={() => handleDateClick(date)}>
              {date !== '' && <span className="date">{date.split('/')[0]}</span>}
            </div>
            
            ))}
          </React.Fragment>
        ))}
      </div>
      {ordersForSelectedDate.length > 0 && (
        <div className="orders-for-date">
          <h3>Orders for {selectedDate.toLocaleDateString()}</h3>
          <ul>
            {ordersForSelectedDate.map(order => (
              <li key={order.id}>
                <strong>Order ID:</strong> {order.id}, <strong>Customer:</strong> {order.customerName}, <strong>Status:</strong> {order.status}
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default OrdersCalendarView;
