import React, { useState } from 'react';
import './App.css';
import Dashboard from './Components/Dashboard';
import ProductsManagement from './Components/ProductsManagement';
import OrdersManagement from './Components/OrdersManagement';
import OrdersCalendarView from './Components/OrdersCalendarView';

function App() {
  const [productCount, setProductCount] = useState(0);

  const handleProductCountChange = (count) => {
    setProductCount(count);
  };

  return (
    <div className="App">
      <Dashboard productCount={productCount} />
      <ProductsManagement onProductCountChange={handleProductCountChange} /> {/* Pass onProductCountChange as a prop */}
      <OrdersManagement />
      <OrdersCalendarView />
    </div>
  );
}

export default App;
